mapper.select:
select

from SYS_USER
where telephone = #{keyword}
or mail = #{keyword}
or username = #{keyword}

select count(1)
from SYS_USER
where mail = #{mail}

  and id != #{id}

select count(1)
from SYS_USER
where telephone = #{telephone}

  and id != #{id}

select t.* from
(
  select rownum rn, id, username, telephone, mail, password, dept_id, status, remark, operator,
  operator_time, operator_ip
  from SYS_USER
  where dept_id = #{dept_id}
) t
where rn between #{page.startRow} and #{page.endRow}

select id, username, telephone, mail, dept_id, status, remark, operator, operator_time
from sys_user
where ID = #{id,jdbcType=DECIMAL}
